import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

function Home() {
  const [disasterData, setDisasterData] = useState([]);

  useEffect(() => {
    // Fetch the data from the API
    fetch('http://localhost:300/disasters')  // Replace with your API URL
      .then((response) => response.json())
      .then((data) => setDisasterData(data))
      .catch((error) => console.error('Error fetching data:', error));
  }, []);

  return (
    <div>
      <div
        className="banner"
        style={{
          position: 'relative',
          color: '#fff',
          fontSize: '24px',
          fontWeight: 'bold',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
          marginBottom: '20px'
        }}
      >
        <img
          src="/image.png"
          alt="Disaster Management Banner"
          style={{
            width: '100%',
            height: 'auto',
            maxHeight: '250px',
            objectFit: 'cover',
            borderRadius: '8px'
          }}
        />
        <div
          style={{
            position: 'absolute',
            bottom: '20px',
            right: '20px',
            padding: '10px 20px',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            borderRadius: '5px'
          }}
        >
          Disaster Management Dashboard
        </div>
      </div>

      <div className="home-container">
        <div className="left-column">
          <h2>Notifications Related To Disasters</h2>
          <Link to="/youtube-videos">Tab for Youtube</Link>
        </div>

        <div className="right-section">
          <h2>Disaster-Related Information</h2>
          <div className="alert-container">
            <div className="blinking-text">Alert!</div>
          </div>

          <div className="disaster-info">
            {disasterData.map((item) => (
              <div key={item._id.$oid} className="disaster-item">
                <p><strong>Severity:</strong> {item.Severity}</p>
                <p><strong>Keyword:</strong> {item.keyword}</p>
                <p><strong>Text:</strong> {item.text}</p>
                <p><strong>Location:</strong> {item.location}</p>
                <hr />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
